import React from "react";
import industrial from "../assests/images/industrial.png";
import construction from "../assests/images/rental.png";
import Heading from "../Components/Core-component/Heading";
import content from './content';
import imagenew from '../assests/images/industrial.png'
import tender from "../assests/images/new-tender.png";
import { Product } from "./serviceproduct";
export default function App(){
const serviceData = [
    {
      id: 1,
      heading: "TENDER",
      subheading: "निविदा",
      image: tender,
      content: "Check out the ongoing invitation of bids for large projects that must be submitted within a finite timeline",
      nwcontent: "BOUNDARY - B/26 / Tender Construction of Boundary Wall, Varanasi Uttar Pradesh ( UP)"
    },
    {
      id: 2,
      heading: "Careers",
      subheading: "नौकरी",
      image: tender,
      content: "Submit your resume here and you would be contacted for the right opportunity.",
      nwcontent: "BOUNDARY - B/26 / Tender Construction of Boundary Wall, Varanasi Uttar Pradesh ( UP)"
    },
    {
      id: 3,
      heading: "Labour Contractor",
      subheading: "लेबर ठेकेदार",
      image: tender,
      content: "कृपया अपने आप को टियारा इन्फ्रास्ट्रक्चर के साथ पंजीकृत करें.",
      nwcontent: "BOUNDARY - B/26 / Tender Construction of Boundary Wall, Varanasi Uttar Pradesh ( UP)"
    }
  ];
//   export default serviceData;
    return(
        <>
        <div className="services-wrp">
            <Heading
            prefixhead = "All"
            suffixhead = "Services" 
            />
        <div className="services-block-wrp container">
            <div className="row">
            {content.map(content =>(
                <Product
                key={content.id}
                image={content.image}
                name={content.name}
                read={content.read}
                />
            ))}
            </div>
        </div>
        </div>
        </>
    )
}
