import React from "react";
import { useState } from "react";
class Login extends React.Component{
    state={
        username:'',
        password:''
    }
    handleChange = (e) =>{

        const{username,value} = e.target
        this.setState({[username]:value})
    }
    handleSubmit = (e) =>{
       e.preventDefault()
    }
    render(){
return (
    <>
       <div id="login">
        <h3 class="text-center text-white pt-5">Login form</h3>
        <div class="container">
            <div id="login-row" class="row justify-content-center align-items-center">
                <div id="login-column" class="col-md-6">
                    <div id="login-box" class="col-md-12">
                        <form id="login-form" class="form" action=""  onSubmit={this.handleSubmit}>
                            <h3 class="text-center text-info">Login</h3>
                            <div class="form-group">
                                <label for="username" class="text-info">Username:</label><br></br>
                                <input type="text" name="username" id="username" class="form-control" required onChange={this.handleChange}/>
                            </div>
                            <div class="form-group">
                                <label for="password" class="text-info">Password:</label><br></br>
                                <input type="text" name="password" id="password" class="form-control" required onChange={this.handleChange}/>
                            </div>
                            <div class="form-group">
                                <label for="remember-me" class="text-info"><span>Remember me</span><span><input id="remember-me" name="remember-me" type="checkbox"/></span></label><br></br>
                                <button onSubmit={this.handleSubmit}>Log In</button>
                            </div>
                            <div id="register-link" class="text-right">
                                <a href="#" class="text-info">Register here</a>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </div>
    </>
    )}
}
export default Login;
