import React from "react";
import Accordion from "./accordian";
const Solution =()=>{
return(
    <>
    <div className="solution-main-wrp container">
       <div className="row">
          <div className="col-md-6 col-sm-6">
              <div className="main-heading">
                <h3><span>Our</span> Solution</h3>
              </div>
              <div className="main-content">
                <p>We believe our employees are our greatest assets. We place enormous value on the welfare and commitment of our employees as well as the superior level of service they provide for our customers. Our employees all benefit from extensive on the job training and our apprentice schemes are amongst the best in the industry. We pride ourselves on many of our staff remaining with us throughout their careers, often progressing from entry level in our depots to senior management positions.</p>
              </div>
              <div className="accordion">
            {accordionData.map(({ title, content}) => (
                <Accordion title = { title } content = {content}/>
            ))}
               </div>
          </div>
          <div className="col-md-6 col-sm-6">

          </div>
       </div>
    </div>
    </>
)
}
export default Solution;
